Module.Content:

#requireResource("com.atlassian.jira.gadgets:common")
#requireResource("com.atlassian.gadgets.publisher:ajs-gadgets")
#requireResource("no.spk.jira:jira-burndown")
#includeResources()

<div id="intro-content"><div class="fullyCentered loading">Loading...</div></div>
<script type="text/javascript">
(function () {
    /* (2) Construct and initialise the gadget */
    var gadget = AJS.Gadget({
      baseUrl: "__ATLASSIAN_BASE_URL__", /* (3) Used to make base url available to gadget */
      useOauth: "/rest/gadget/1.0/currentUser",
      view: {
        //onResizeAdjustHeight: true,
        //onResizeReload: true,
        enableReload: true,
        template: function(args) {
          var gadget = this;
          var prefs = new gadgets.Prefs();
          var title = prefs.getString("Title");
          if (title) {
            gadgets.window.setTitle(title);
          }
          var versionId = prefs.getInt("VersionId");
          var typeId = prefs.getInt("TypeId");
          var height = prefs.getInt("Height");
          var width = prefs.getInt("Width");
          var includeLegend = prefs.getBool("IncludeLegend");
          var includeTrendLine = prefs.getBool("IncludeTrendLine");
          var startDate = prefs.getString("StartDate");
          var path = "/secure/RunPortlet.jspa?portletKey=com.laughingpanda.jira:versionWorkloadChart&chart.height="
            + height + "&chart.width=" + width + "&startDate=" + startDate + "&versionId=" + versionId + "&typeId=" + typeId
            + "&chart.includeLegend=" + includeLegend + "&chart.includeTrendLine=" + includeTrendLine;

          AJS.$.ajax({
            url: path,
            type: "GET",
            // data: ({projectsOnly : "true"}),
            dataType: "text",
            success: function(msg) {
              html = JBD.stripPortletOutputDecoration(msg);
              AJS.$("#intro-content").removeClass("loading").html(html);
            },
            error: function(msg) {
              // alert(msg);
            }
          });
        }
      }
    });
  })();
</script>
